<form action="http://muhibindiancuisine.com/123/subscribe.php" method="post">
  <input type="text" class="input_subscribe" name="email" value="Enter your email(optional)" onblur="if (this.value == '') {this.value = 'Enter your email(optional)';}" onfocus="if (this.value == 'Enter your email(optional)') {this.value = '';}">
  <input type="text" class="input_subscribe" name="number" value="Enter your mobile number" onblur="if (this.value == '') {this.value = 'Enter your mobile number';}" onfocus="if (this.value == 'Enter your mobile number') {this.value = '';}">
  <input type="submit" value="Send">
</form>
